import 'codemirror';
import type { Mode } from 'codemirror';

declare module 'codemirror' {
    // Based on https://codemirror.net/doc/manual.html#addon_multiplex
    interface Other {
        open: string | RegExp;
        close: string | RegExp;
        mode: Mode<unknown>;
        delimStyle?: string;
        innerStyle?: string;
        parseDelimiters?: boolean;
    }

    function multiplexingMode(outer: Mode<unknown>, ...others: readonly Other[]): Mode<unknown>;
}
